// src/components/HamburgerMenu/HamburgerMenu.jsx

import React, { useState } from 'react';
import styles from './HamburgerMenu.module.scss'; // For CSS Modules

const HamburgerMenu = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(prevState => !prevState);
    };

    return (
        <nav className={styles.navbar}>
            <button className={`${styles.hamburger} ${isOpen ? styles.active : ''}`} onClick={toggleMenu} aria-label="Toggle Navigation">
                <span className={styles.line}></span>
                <span className={styles.line}></span>
                <span className={styles.line}></span>
            </button>
            {isOpen && (
                <ul className={styles['nav-menu']}>
                    <li><a href="#">About</a></li>
                    <li><a href="#">Projects</a></li>
                    <li><a href="#">Contact</a></li>
                </ul>
            )}
        </nav>
    );
};

export default HamburgerMenu;
